bool test(const std::string& name, bool notifyOnSuccess)
{
	typedef std::map<std::string, std::function<bool()>> tests_type;
	static tests_type tests =
	{
		tests_type::value_type("basic", testBasic),
		tests_type::value_type("every", [&]()
		{
			return std::all_of(tests.begin(), tests.end(), [&](tests_type::value_type& x)
			{
				if(x.first == "every")
					return true;
				return test(x.first, false);
			});
		})
	};
	auto it = tests.find(name);
	if(it != tests.end())
	{
		if(it->second())
		{
			if(notifyOnSuccess)
				std::cout << name << " test PASS\n";
			return true;
		}
		else
		{
			std::cout << name << " test FAIL\n";
			return false;
		}
	}
	else
	{
		std::cout << "No such test\n";
		return false;
	}
}
